mapper.select:
select
	id,
	code,
	name,
	create_time,
	create_user,
	update_time,
	update_user
from c_type


		and id >= #{idSearchBegin,jdbcType=INTEGER}


		and id <= #{idSearchEnd,jdbcType=INTEGER}


		and code like CONCAT('%', #{code,jdbcType=VARCHAR}, '%')


		and name like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


		and create_time >= #{createTimeSearchBegin,jdbcType=TIMESTAMP}


		and create_time <= #{createTimeSearchEnd,jdbcType=TIMESTAMP}


		and create_user like CONCAT('%', #{createUser,jdbcType=VARCHAR}, '%')


		and update_time >= #{updateTimeSearchBegin,jdbcType=TIMESTAMP}


		and update_time <= #{updateTimeSearchEnd,jdbcType=TIMESTAMP}


		and update_user like CONCAT('%', #{updateUser,jdbcType=VARCHAR}, '%')


order by update_time desc
limit #{offset}, #{rows}

select count(1)
from c_type


		and id >= #{idSearchBegin,jdbcType=INTEGER}


		and id <= #{idSearchEnd,jdbcType=INTEGER}


		and code like CONCAT('%', #{code,jdbcType=VARCHAR}, '%')


		and name like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


		and create_time >= #{createTimeSearchBegin,jdbcType=TIMESTAMP}


		and create_time <= #{createTimeSearchEnd,jdbcType=TIMESTAMP}


		and create_user like CONCAT('%', #{createUser,jdbcType=VARCHAR}, '%')


		and update_time >= #{updateTimeSearchBegin,jdbcType=TIMESTAMP}


		and update_time <= #{updateTimeSearchEnd,jdbcType=TIMESTAMP}


		and update_user like CONCAT('%', #{updateUser,jdbcType=VARCHAR}, '%')

mapper.delete:
delete from c_type


		and id = #{id,jdbcType=INTEGER}


		and code = #{code,jdbcType=VARCHAR}


		and name = #{name,jdbcType=VARCHAR}


		and create_time = #{createTime,jdbcType=TIMESTAMP}


		and create_user = #{createUser,jdbcType=VARCHAR}


		and update_time = #{updateTime,jdbcType=TIMESTAMP}


		and update_user = #{updateUser,jdbcType=VARCHAR}

mapper.update:
update c_type set
	id = #{id,jdbcType=INTEGER}
	,code = #{code,jdbcType=VARCHAR}
	,name = #{name,jdbcType=VARCHAR}

	,create_time = #{createTime,jdbcType=TIMESTAMP}


	,create_user = #{createUser,jdbcType=VARCHAR}


	,update_time = #{updateTime,jdbcType=TIMESTAMP}


	,update_user = #{updateUser,jdbcType=VARCHAR}

where id = #{id,jdbcType=INTEGER}
